function Validation(values) {
    let error = {};
    const e_pat = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const p_pat = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9]{8,}$/;
  
    // Validate Name
    if (values.name === "") {
      error.name = "Name should not be empty";
    }
  
    // Validate Email
    if (values.email === "") {
      error.email = "Email should not be empty";
    } else if (!e_pat.test(values.email)) {
      error.email = "Email format is invalid";
    }
  
    // Validate Password
    if (values.password === "") {
      error.password = "Password should not be empty";
    } else if (!p_pat.test(values.password)) {
      error.password = "Password must be at least 8 characters long, include one uppercase letter, one lowercase letter, and one number";
    }
  
    return error;
  }
  
  export default Validation;
